mapper.select:
select o.courseid prop,o.courseName label from course o join (select cc.courseId,className from classcourse cc
join class s on s.classid=cc.classid

    className=#{className}

) m on m.courseId=o.courseId

select a.*,b.* from (select s.stuname,s.sex,s.university,s.birthplace,s.className,s.state,m.* from student s left join (select stuid
sum(case when courseid = #{course.courseId} then score end) z${course.courseId}
from score group by stuid)m on s.stuid=m.stuid
s.className=#{className}
) a left join (select m.evlScore,m.evlContent,(select userName from user where userId = m.userId) userName,x.* from school_evl m join ( select j.stuid,y1.sumScore s1,y2.sumScore s2,y3.sumScore s3,y4.sumScore s4 from job_evaluation j left join(select stuid,sumScore from job_evaluation where dateId=0)y1 on y1.stuid=j.stuid left join(select stuid,sumScore from job_evaluation where dateId=1)y2 on y2.stuid=y1.stuid left join(select stuid,sumScore from job_evaluation where dateId=2)y3 on y3.stuid=y2.stuid left join(select stuid,sumScore from job_evaluation where dateId=3)y4 on y4.stuid=y3.stuid group by j.stuid) x on m.stuid=x.stuid) b on a.stuid=b.stuid
state!=4
and stuname like '%' #{nameStr} '%'
limit #{counts},#{pageSize}

select a.*,b.* from (select s.stuname,s.sex,s.university,s.birthplace,s.className,s.state,m.* from student s left join (select stuid
sum(case when courseid = #{course.courseId} then score end) z${course.courseId}
from score group by stuid)m on s.stuid=m.stuid
s.className=#{className}
) a left join (select m.evlScore,m.evlContent,(select userName from user where userId = m.userId) userName,x.* from school_evl m join ( select j.stuid,y1.sumScore s1,y2.sumScore s2,y3.sumScore s3,y4.sumScore s4 from job_evaluation j left join(select stuid,sumScore from job_evaluation where dateId=0)y1 on y1.stuid=j.stuid left join(select stuid,sumScore from job_evaluation where dateId=1)y2 on y2.stuid=y1.stuid left join(select stuid,sumScore from job_evaluation where dateId=2)y3 on y3.stuid=y2.stuid left join(select stuid,sumScore from job_evaluation where dateId=3)y4 on y4.stuid=y3.stuid group by j.stuid) x on m.stuid=x.stuid) b on a.stuid=b.stuid
state!=4
and stuname like '%' #{nameStr} '%'
